mapper.select:
select a.id, a.user_auth_id, a.category_id, a.title, left(a.content,300) as content, a.cover, a.type, a.original_author, a.original_title, a.original_url, a.note, a.top, a.status, a.create_time, a.update_time, a.deleted,
c.id as c_id, c.name as c_name, c.hidden as c_hidden,
t.id as t_id, t.name as t_name, t.hidden as t_hidden from t_article a left join t_category c on c.id=a.category_id and c.deleted=0 and c.hidden=0 left join t_article_tag at on at.article_id=a.id left join t_tag t on t.id=at.tag_id and t.deleted=0 and t.hidden=0 where a.deleted=0
and a.status=#{article.status}
and a.type != #{article.type}
and c.id=#{article.categoryId}
and a.id not in ( select distinct article_id as id from t_article_tag at right join t_tag t on at.tag_id = t.id and t.deleted=0 where t.hidden=1
or t.id!=#{article.tagId}
) order by top desc, a.update_time desc limit #{current},#{limit}

select distinct count(id) from (select distinct a.id from t_article a left join t_category c on c.id=a.category_id and c.deleted =0 and c.hidden=0 left join t_article_tag at on at.article_id=a.id left join t_tag t on t.id=at.tag_id and t.deleted=0 and t.hidden=0 where a.deleted=0
and a.status=#{article.status}
and a.type != #{article.type}
and c.id=#{article.categoryId}
and a.id not in ( select distinct article_id as id from t_article_tag at right join t_tag t on at.tag_id = t.id and t.deleted=0 where t.hidden=1
or t.id != #{article.tagId} ) ) as ids

select a.id, a.user_auth_id, a.category_id, a.title, a.content, a.cover, a.type, a.original_author, a.original_title, a.original_url, a.note, a.top, a.status, a.create_time, a.update_time,
c.id as c_id, c.name as c_name, c.hidden as c_hidden,
t.id as t_id, t.name as t_name, t.hidden as t_hidden from t_article a left join t_category c on c.id = a.category_id and c.deleted =0 and c.hidden=0 left join t_article_tag at on at.article_id=a.id left join t_tag t on t.id= at.tag_id and t.deleted=0 and t.hidden=0 where a.deleted=0 and a.id=#{id}
and a.status=#{article.status}
and a.type != #{article.type}

select distinct a.id, a.user_auth_id, a.category_id, a.title, a.cover, a.type, a.original_author, a.original_title, a.original_url, a.note, a.top, a.status, a.create_time, a.update_time, a.deleted,
c.id as c_id, c.name as c_name, c.hidden as c_hidden from t_article a left join t_category c on c.id = a.category_id and c.deleted =0 and c.hidden=0 left join t_article_tag at on at.article_id=a.id left join t_tag t on t.id= at.tag_id and t.deleted=0 and t.hidden=0 where a.deleted=0
and a.status=#{article.status}
and a.type != #{article.type}
and a.id not in ( select distinct article_id as id from t_article_tag at left join t_tag t on at.tag_id = t.id and t.deleted=0 where t.hidden=1 ) order by a.create_time desc limit #{current}, #{limit}

select a.id, a.user_auth_id, a.category_id, a.title, a.cover, a.type, a.original_author, a.original_title, a.original_url, a.note, a.top, a.status, a.create_time, a.update_time, a.deleted, c.id as c_id, c.name as c_name, c.hidden as c_hidden, t.id as t_id, t.name as t_name, t.hidden as t_hidden from t_article a left join t_category c on c.id=a.category_id and c.deleted =0 left join t_article_tag at on at.article_id=a.id left join t_tag t on t.id= at.tag_id and t.deleted=0 where a.deleted=0
and a.title like concat('%',#{article.title},'%')
and a.type=#{article.type}
and a.status=#{article.status}
and c.id=#{article.categoryId}
and a.top=#{article.top}
and a.id in (select distinct at.article_id as id from t_article_tag at left join t_tag t on at.tag_id=t.id where at.tag_id =#{article.tagId})
order by a.update_time desc limit #{current}, #{limit}

select count(distinct a.id) as count from t_article a left join t_category c on c.id=a.category_id and c.deleted =0 left join t_article_tag at on at.article_id=a.id left join t_tag t on t.id= at.tag_id and t.deleted=0 where a.deleted=0
and a.title like concat('%',#{article.title},'%')
and a.type=#{article.type}
and a.status=#{article.status}
and c.id=#{article.categoryId}
and a.top=#{article.top}
and a.id in (select distinct at.article_id as id from t_article_tag at left join t_tag t on at.tag_id=t.id where at.tag_id =#{article.tagId})
