mapper.select:
select c.id, c.user_auth_id, c.topic_type, c.topic_id, c.content, c.ip_address, c.ip_source, c.device, c.browser, c.point, c.location, c.root_id, c.parent_id, c.type, c.nickname, c.email, c.qq, c.hidden, c.review, c.create_time, c.update_time, c.deleted, info.nickname as infoName, info.avatar as infoAvatar, info.email as infoEmail, info.website as infoWebsite, info.introduction as introduction, r.id as roleId, r.name as roleName from (select co.id from ( select r.id as id from t_comment r where r.topic_type=#{comment.topicType}
and r.topic_id=#{comment.topicId}
and r.parent_id=0 and r.deleted=0 and r.hidden=0 and r.review=1 order by create_time asc limit #{current},#{limit} ) as r,t_comment co where r.id=co.id ) as root left join t_comment as c on root.id=c.id left join t_user_auth auth on c.user_auth_id = auth.id left join t_user_info info on auth.user_info_id=info.id left join t_user_role ur on auth.id=ur.user_auth_id left join t_role r on r.id=ur.role_id order by c.create_time asc

select count(c.id) as count from t_comment c where c.topic_type=#{comment.topicType}
and c.topic_id=#{comment.topicId}
and c.deleted=0 and c.hidden=0 and c.parent_id=0 and c.review=1

select count(c.id) as count from t_comment c where c.topic_type=#{comment.topicType}
and c.topic_id=#{comment.topicId}
and c.deleted=0 and c.hidden=0 and c.review=1

select c.id, c.user_auth_id, c.topic_type, c.topic_id, c.content, c.ip_address, c.ip_source, c.device, c.browser, c.point, c.location, c.root_id, c.parent_id, c.type, c.nickname, c.email, c.qq, c.hidden, c.review, c.create_time, c.update_time, info.nickname as infoName from t_comment as c left join t_user_auth auth on c.user_auth_id = auth.id left join t_user_info info on auth.user_info_id=info.id where c.deleted=0
and c.id=#{comment.id}
and c.userAuthId=#{comment.userAuthId}
and c.topic_type=#{comment.topicType}
and c.topic_id=#{comment.topicId}
and c.content like concat('%',#{comment.content},'%')
and c.ipSource like concat('%',#{comment.ipSource},'%')
and c.device like concat('%',#{comment.device},'%')
and c.browser like concat('%',#{comment.browser},'%')
and c.location like concat('%',#{comment.location},'%')
and c.rootId=#{comment.rootId}
and c.parentId=#{comment.parentId}
and c.email like concat('%',#{comment.email},'%')
and c.qq like concat('%',#{comment.qq},'%')
and c.hidden=#{comment.hidden}
and c.type=#{comment.type}
and (c.nickname like concat('%',#{comment.nickname},'%') or info.nickname like concat('%',#{comment.nickname},'%'))
and c.review=#{comment.review}
and c.create_time between #{comment.endDate} and #{comment.beginDate}
order by c.create_time desc limit #{current},#{limit}

select
count(c.id) as count
from
t_comment as c
left join t_user_auth auth on c.user_auth_id = auth.id
left join t_user_info info on auth.user_info_id=info.id
where
c.deleted=0

    and c.id=#{comment.id}


    and c.userAuthId=#{comment.userAuthId}


    and c.topic_type=#{comment.topicType}


    and c.topic_id=#{comment.topicId}


    and c.content like concat('%',#{comment.content},'%')


    and c.ipSource like concat('%',#{comment.ipSource},'%')


    and c.device like concat('%',#{comment.device},'%')


    and c.browser like concat('%',#{comment.browser},'%')


    and c.location like concat('%',#{comment.location},'%')


    and c.rootId=#{comment.rootId}


    and c.parentId=#{comment.parentId}


    and c.email like concat('%',#{comment.email},'%')


    and c.qq like concat('%',#{comment.qq},'%')


    and c.hidden=#{comment.hidden}


    and c.type=#{comment.type}


    and (c.nickname like concat('%',#{comment.nickname},'%') or info.nickname like
    concat('%',#{comment.nickname},'%'))


    and c.review=#{comment.review}


    and c.create_time between #{comment.endDate} and #{comment.beginDate}
